def collatz_conjecture(number, step=0):
    while(number != 1):
        if (number % 2 == 0):
            #print('Even therefore ' + str(number))
            number = number // 2
            step+=1
            
        else:
            #print('Odd therefore ' + str(number))
            number = 3 * number + 1
            step+=1
    print('Success!')
    return(step)

